  { image: "/images/birthday.jpg", caption: "ÿπŸäÿØ ŸÖŸäŸÑÿßÿØŸÉ üéÇ" },
  { image: "/images/beach.jpg", caption: "ÿ£ÿ¨ŸÖŸÑ ŸäŸàŸÖ ÿπŸÑŸâ ÿßŸÑÿ®ÿ≠ÿ± üêö" },
  { image: "/images/cafe.jpg", caption: "ŸÇŸáŸàÿ™ŸÜÿß ÿ≥Ÿàÿß ‚òï" },
];

export default function GallerySection() {
  return (
    <section className="bg-[#fffaf5] py-16 px-4">
      <div className="max-w-6xl mx-auto text-center mb-10">
        <h2 className="text-3xl font-bold text-[#4a2c1f] mb-4">ÿ∞ŸÉÿ±Ÿäÿßÿ™ŸÜÿß</h2>
        <p className="text-[#8b5e3c]">ŸÑÿ≠ÿ∏ÿßÿ™ ŸÖÿ≠ŸÅŸàÿ±ÿ© ŸÅŸä ÿßŸÑŸÇŸÑÿ® ‚ù§Ô∏è</p>
